from exofop.download import System, SystemDownloaderfrom exofop.extract import LightCurveTableListfrom pathlib import Pathfrom exofop.extract import LightCurveTableList, LightCurveTable, SynonymMap, SynonymMapLc# For nice logs, we reformat the root loggger's StreamHandlerfrom exofop.utils.logger import configure_root_loggerconfigure_root_logger()data_dir = "data/Exofop"system = System("TOI 1130")  # or system = System("TIC 12345678")system_loader = SystemDownloader(    system=system,    data_dir=data_dir,)tags = system_loader.time_series.tagssystem_loader.download(tags[:5], unzip=True)target_dir = system_loader.target_dirlctl = LightCurveTableList.load_exofop_data(    target_dir=target_dir,)# Standardize the column names for consistency and apply time correction if neededlctl.standardise_column_names()lctl.apply_time_correction_in_case_of_discrepancy()# Check if some of the extracted light curves could not be standardised successfullylctl.incompletelctl.info_dfsave_dir = save_dir = "/Users/carmencardenas/Documents/GitHub/Astro502_sp26/exofop_outputs"save_dir = Path("/Users/carmencardenas/Documents/GitHub/Astro502_sp26/exofop_outputs")save_dir.mkdir(parents=True, exist_ok=True)lctl.save(str(save_dir))lctl.save(save_dir)# Initialise a synonym map from a dictionarysynonym_map = SynonymMap()# Define a primary alias and its synonymssynonym_map['primary_alias_a'] = ['synonym_a_0', 'synonym_a_1', 'synonym_a_2']# Alternatively, we can use `update` to add multiple primary aliases at oncesynonym_map.update(    {        'primary_alias_b': ['synonym_b_0', 'synonym_b_1', 'synonym_b_2'],        'primary_alias_c': ['synonym_c_0'],    })# If we realise that we were missing a synonym of `primary_alias_a`, we can add itsynonym_map.add_synonyms('primary_alias_a', 'synonym_a_3')# And if we want to rename `primary_alias_b` to `primary_alias_b_new`, we can do sosynonym_map.rename_primary_alias('primary_alias_b', 'new_primary_alias_b_new')synonym_maptarget_dir = './TOI_1130'  # As generated in the previous tutorialexofop_data = LightCurveTableList.load_exofop_data(        target_dir=target_dir,        observation_names=None,  # defaults to all folders in data_dir        allow_multiple_filetypes=True,    )exofop_dataexofop_data[-1][:5]exofop_data[0].metasimple_synonym_map = SynonymMapLc()simple_synonym_map.update(    {        'BJD_TDB': ('time', 'TIME', 'BJD', 'BJD_TDB_MOBS', '#BJD_TDB'),        'rel_flux_T1_n': ('flux', 'FLUX', 'rel_flux', 'rel_flux_T1'),        'rel_flux_err_T1_n': ('flux_err', 'ERRFLUX', 'rel_flux_err_T1')    })print(simple_synonym_map.light_curve_attributes)print(simple_synonym_map)exofop_data.standardise_column_names(synonym_map=simple_synonym_map)exofop_data.apply_time_correction_in_case_of_discrepancy()exofop_data.info_df