import matplotlib.pyplot as pltfrom astropy.io import fitspath = "/Users/carmencardenas/Desktop/ADP.2020-06-10T14_26_50.822.fits"hdul = fits.open(path)data = hdul["SPECTRUM"].data   wave = data["WAVE"][0]                 flux = data["FLUX_REDUCED"][0]        plt.figure(figsize=(10,4))plt.plot(wave, flux, linewidth=0.7)plt.xlabel("Wavelength")plt.ylabel("Flux")plt.title("Spectrum")plt.tight_layout()plt.show()hdul.close()